import React,{useState} from 'react';
import './css/Login.css';

const Login = ({setLogin}) => {
    const [logorsign,setLogorSign]= useState("login");
    const loginToggle = () => {
        setLogorSign("login")
    }
    const signupToggle = () => {
        setLogorSign("signup")
    }
    const submitLogin = () => {
        setLogin(true);
    }

    return(
        <>
        <div id="loginpage">
            <div className="loginselector">
                <button onClick={loginToggle}>Login</button><button onClick={signupToggle}>Sign Up</button>
            </div>
            <div className={logorsign==="login"?"login":"loginNone"}>
                <h1>Login:</h1>
                <form onSubmit={submitLogin}>
                    <label htmlFor='username'>Username:</label><br/>
                    <input type='text' placeholder='username'></input><br/>
                    <label htmlFor='password'>Password:</label><br/>
                    <input type='password' placeholder='your password'></input><br/>
                    <input type='submit'/>
                </form>
            </div>
            <div className={logorsign==="login"?"signupNone":"signup"}>
                <h1>Sign Up:</h1>
                <form onSubmit={submitLogin}>
                    <label htmlFor='email'>Email:</label><br/>
                    <input type='email' placeholder='your email'></input><br/>
                    <label htmlFor='username'>Username:</label><br/>
                    <input type='text' placeholder='username'></input><br/>
                    <label htmlFor='password'>Password:</label><br/>
                    <input type='password' placeholder='your password'></input><br/>
                    <label htmlFor='confirmPassword'>Confirm password:</label><br/>
                    <input type='password' placeholder='confirm password'></input><br/>
                    <input type='submit'/>
                </form>
            </div>
        </div>
        </>
    )
}

export default Login;
